import React, {useState} from 'react';

const PostsAdd = () => {
	const [title, setTitle] = useState('');
	const [description, setDescription] = useState('');
	const [price, setPrice] = useState('');
	const [willDeliver, setWillDeliver] = useState(false);
	const [location, setLocation] = useState('');

	const handleSubmit = (ev) => {
		ev.preventdefault();
		console.log("description: ", description);
	}

	return <>
		<form onSubmit={handleSubmit}>
			<input type='text' value={title} onChange={(ev) => setTitle(ev.target.value)} placeholder="title"></input>
			<textarea value={description} onChange={(ev) => setDescription(ev.target.value)} placeholder="description"></textarea>
			<input type='text' value={price} onChange={(ev) => setPrice(ev.target.value)} placeholder="price"></input>
			<input type='text' value={location} onChange={(ev) => setLocation(ev.target.value)} placeholder="location"></input>
			<input type='radio' id='willDeliverTrue' name='willDeliver' value={true}></input>
			<label for='willDeliver'>Yes</label>
			<input type='radio' id='willDeliverFalse' name='willDeliver' value={false}></input>
			<label for='willNotDeliver'>No</label>
			<button type="submit">Submit Post</button>
		</form>
	</>
}

export default PostsAdd;
